#include <stdio.h>

int rfactorial(int n)
{
        if (n == 1)
                return 1;
        return n * rfactorial(n - 1);
}

int factorial(int n)
{
        int total = 1;
        for (int i = n; i >= 1; --i)
                total *= i;
        return total;
}

int main(void)
{
        const int nr = 5;
        printf("%d\n", rfactorial(nr));
        printf("%d\n", factorial(nr));

        return 0;
}

